import React from 'react';
import axios from 'axios';
import './App.css';
import { Transaction } from './stories/Transaction';
import { TransactionList } from './stories/TransactionList';

interface AppState {
  transactionsError: boolean;
  transactions: Transaction[];
}

class App extends React.Component<any, AppState> {
  constructor(props: any) {
    super(props);
    this.state = {
      transactionsError: false,
      transactions: []};
  }

  componentDidMount() {
    axios.get(`${process.env.REACT_APP_BACKEND_URL}/transactions`).then(res => {
      const transactions = res.data;
      this.setState({transactionsError: false, transactions: transactions});
    }).catch(() => {
      this.setState({transactionsError: true, transactions: []});
      console.error('Something went wrong. You should retry');
    });
  }

  render() {
    let body;
    if (this.state.transactions.length) body = <TransactionList transactions={this.state.transactions}/>;

    return (
      <div className="App">
        <h1>Accounting Notebook</h1>
        {body}
      </div>
    );
  }
}

export default App;
