import React from 'react';
import axios from 'axios';
import './App.css';
import { Transaction } from './stories/Transaction';
import { TransactionList } from './stories/TransactionList';

interface AppState {
  transactionsReady: boolean;
  transactions: Transaction[];
}

class App extends React.Component<any, AppState> {
  constructor(props: any) {
    super(props);
    this.state = {
      transactionsReady: false,
      transactions: []};
  }

  componentDidMount() {
    axios.get(`${process.env.REACT_APP_BACKEND_URL}/transactions`).then(res => {
      const transactions = res.data;
      this.setState({transactionsReady: true, transactions: transactions});
    }).catch(() => {
      console.error('Something went wrong. You should retry');
    });
  }

  render() {
    let body;
    if (this.state.transactionsReady) body = <TransactionList transactions={this.state.transactions}/>;

    return (
      <div className="App">
        <h1>Accounting Notebook</h1>
        {body}
      </div>
    );
  }
}

export default App;
